power_user.custom_css:
#chat {
    .mes {
        align-items: stretch;
        .mesAvatarWrapper {
            display: flex;
            flex-direction: column-reverse;
            align-content: flex-end;
            padding-bottom: 0;
            > .avatar {
                flex: 5 5 auto;
            }
        }
        .mes_block {
            > .ch_name {
                color: rgb(234, 234, 234);
                position: absolute;
                bottom: 0em;
                max-width: 80%;
                .name_text {
                    font-size: calc(var(--mainFontSize) * 0.7);
                }
            }
        }
    }
    .mes[is_user="true"] {
        flex-direction: row-reverse;
        .mes_block {
            > .ch_name {
                right: 4.5em;
            }
        }
    }
    .mes[is_user="false"] {
        .mes_block {
            > .ch_name {
                left: 4.5em;
            }
        }
    }
}

