custom_css:
#chat .mes * {
    text-shadow: none;
}
#chat {
    .mes {
        align-items: stretch;
        .mesAvatarWrapper {
            display: flex;
            flex-direction: column-reverse;
            align-content: flex-end;
            padding-bottom: 0;
            > .avatar {
                flex: 0 0 auto;
            }
        }
        .mes_block {
            width: 80%;
            border-radius: 10px;
            margin-bottom: 2em;
            position: relative;
            overflow: visible;
            &:after {
                content: "";
                position: absolute;
                bottom: -10px;
                height: 20px;
                width: 20px;
            }
            > .ch_name {
                color: rgb(234, 234, 234);
                position: absolute;
                bottom: -1em;
                max-width: 80%;
                .name_text {
                    font-size: calc(var(--mainFontSize) * 0.7);
                }
            }
        }
    }
    .mes[is_user="true"] {
        flex-direction: row-reverse;
        .mes_block {
            background: rgb(165, 174, 180);
            opacity: 85%;
            color: black;
            margin-right: 1em;
            &:after {
                right: -10px;
                background: rgb(165, 174, 180);
                clip-path: polygon(0% 50%, 50% 0%, 100% 100%);
            }
            > .ch_name {
                right: 1em;
            }
        }
    }
    .mes[is_user="false"] {
        .mes_block {
            background: rgb(165, 174, 180);
            opacity: 85%;
            color: black;
            margin-left: 1em;
            &:after {
                background: rgb(165, 174, 180);
                left: -10px;
                clip-path: polygon(50% 0%, 100% 50%, 0% 100%);
            }
            > .ch_name {
                left: 1em;
            }
        }
    }
}
